mapper.select:
select * from (
select a.*,rownum n from
(
select a.corecompanyid,a.bankid,a.contractid,a.lastoperationid,a.quotetemplate,a.creditlimits,
to_char(a.nonrecoursediscount,'fm99990.0000') nonrecoursediscount,to_char(a.nonrecourseinterestrate,'fm99990.0000') nonrecourseinterestrate,
to_char(a.recoursediscount,'fm99990.0000') recoursediscount,to_char(a.recourseinterestrate,'fm99990.0000') recourseinterestrate,a.description,
b.org_name corecompanyidname,c.org_name bankidname
from T_BIGCONTRACTS a,tsys_organization b,tsys_organization c
where a.corecompanyid=b.org_id and a.bankid=c.org_id

    and a.corecompanyid = #{corecompanyid,jdbcType=VARCHAR}


    and a.bankid = #{bankid,jdbcType=VARCHAR}


    and a.contractid = #{contractid,jdbcType=VARCHAR}


    and a.lastoperationid = #{lastoperationid,jdbcType=VARCHAR}


    and a.quotetemplate = #{quotetemplate,jdbcType=VARCHAR}

)a
where 1=1 and rownum <=  #{rows} * #{page}
)
where n  > (#{page} - 1) * #{rows}

select count(*)
from T_BIGCONTRACTS a,tsys_organization b,tsys_organization c
where a.corecompanyid=b.org_id and a.bankid=c.org_id

    and a.corecompanyid = #{corecompanyid,jdbcType=VARCHAR}


    and a.bankid = #{bankid,jdbcType=VARCHAR}


    and a.contractid = #{contractid,jdbcType=VARCHAR}


    and a.lastoperationid = #{lastoperationid,jdbcType=VARCHAR}


    and a.quotetemplate = #{quotetemplate,jdbcType=VARCHAR}
