mapper.select:
select * from (
select a.*,rownum n from
(
select a.* ,b.org_name orgname,t1.c_caption accountcategoryname,t2.c_caption accountstatename
from T_TRADEACCOUNTS a,tsys_organization b,
TDICTIONARY t1,TDICTIONARY t2
where a.orgid=b.org_id
and a.accountcategory=t1.c_keyvalue and t1.l_keyno='9999069'
and a.accountstate=t2.c_keyvalue and t2.l_keyno='9999070'

    and a.accountcategory = #{accountcategory,jdbcType=VARCHAR}


    and a.accountstate = #{accountstate,jdbcType=VARCHAR}


    and a.orgid =#{orgid,jdbcType=VARCHAR}

)  a
where 1=1 and rownum <=  #{rows} * #{page}
)
where n  > (#{page} - 1) * #{rows}

select count(*)
from T_TRADEACCOUNTS a,tsys_organization b,
TDICTIONARY t1,TDICTIONARY t2
where a.orgid=b.org_id
and a.accountcategory=t1.c_keyvalue and t1.l_keyno='9999069'
and a.accountstate=t2.c_keyvalue and t2.l_keyno='9999070'

    and a.accountcategory = #{accountcategory,jdbcType=VARCHAR}


    and a.accountstate = #{accountstate,jdbcType=VARCHAR}


    and a.orgid =#{orgid,jdbcType=VARCHAR}

select * from (
select a.*,rownum n from
(
select a.postdatetime,to_char(a.ledgeramount,'fm9999999999990.00') ledgeramount,b.accountname,t1.c_caption operateflagname,t2.c_caption approvestatename
from T_TRADETRANSACTIONS a,t_tradeaccounts b ,
tdictionary t1,tdictionary t2
where a.tradeaccountsid=b.urid
and t1.c_keyvalue=a.operateflag and t1.l_keyno='9999074'
and t2.c_keyvalue=a.approvestate and t2.l_keyno='1021'

    and a.operateflag = #{operateflag,jdbcType=VARCHAR}


    and a.ledgeramount like  concat('%',concat(#{ledgeramount,jdbcType=VARCHAR},'%'))

)  a
where 1=1 and rownum <=  #{rows} * #{page}
)
where n  > (#{page} - 1) * #{rows}

select count(*)
from T_TRADETRANSACTIONS a,t_tradeaccounts b ,
tdictionary t1,tdictionary t2
where a.tradeaccountsid=b.urid
and t1.c_keyvalue=a.operateflag and t1.l_keyno='9999074'
and t2.c_keyvalue=a.approvestate and t2.l_keyno='1021'

    and a.operateflag = #{operateflag,jdbcType=VARCHAR}


    and a.ledgeramount = #{ledgeramount,jdbcType=VARCHAR}

select * from (
select a.*,rownum n from
(
select a.availablebalance,a.balance,a.closingbalance,b.accountname
from T_TRADEBALANCES a ,t_tradeaccounts b
where a.tradeaccountsid=b.urid

    and a.availablebalance like  concat('%',concat(#{availablebalance,jdbcType=VARCHAR},'%'))


    and a.balance like  concat('%',concat(#{balance,jdbcType=VARCHAR},'%'))


    and a.closingbalance concat('%',concat(#{closingbalance,jdbcType=VARCHAR},'%'))

)  a
where 1=1 and rownum <=  #{rows} * #{page}
)
where n  > (#{page} - 1) * #{rows}
